import React, { useEffect } from 'react';
import app from 'firebase/app';
import Firebase from '../Firebase/firebase';
import PaymentForm from '../Plans/PaymentForm';

 
function Plans () {
  const db = app.firestore()
  var storage = app.storage();
  var gsReference = storage.refFromURL('gs://academie-90dfd.appspot.com/Profile.jpg')

  gsReference.getDownloadURL().then(function(url) {
    // `url` is the download URL for 'images/stars.jpg'
  
    // This can be downloaded directly:
    var xhr = new XMLHttpRequest();
    xhr.responseType = 'blob';
    xhr.onload = function(event) {
      var blob = xhr.response;
    };
    xhr.open('GET', url);
    xhr.send();
  
  });

  const [fileUrl, setFileUrl] = React.useState(null)
  const [users, setUsers] = React.useState([])

  const onFileChange = async(e) => {
    const file = e.target.files[0]
    const storageRef = app.storage().ref()
    const fileRef = storageRef.child(file.name)
    await fileRef.put(file)
    setFileUrl(await fileRef.getDownloadURL())
 
  }

  const onSubmit = (e) =>{
    e.preventDefault()
    const username = e.target.username.value;
    if(!username){
      return
    }
    db.collection("users").doc(username).set({
      name: username,
      avatar: fileUrl
    })
  }

  useEffect(() => {
    const fetchUsers = async () => {
      const usersCollection = await db.collection('users').get()
      setUsers(usersCollection.docs.map(doc => {
        return doc.data()
      }))
    }
    fetchUsers()
  }, [])


  return(
    <>
    <div>
      <PaymentForm />
    </div>
      <form onSubmit={onSubmit}>
        <input type="file" onChange={onFileChange}/>
        <input type="text" name="username" placeholder="Name"/>
        <button>Submit</button>
      </form>
      <form >
        <button>
          <a href={"https://firebasestorage.googleapis.com/v0/b/academie-90dfd.appspot.com/o/resurse%20site.zip?alt=media&token=7e387663-3a23-4a0c-b955-85aa1da9ba06"}>
          Download
        </a>
        </button>     
      </form>
      <ul>
          {users.map((user) => {
            return (
            <li key={user.name}>
              <img width="100" height="100" src={user.avatar} alt={user.name}/>
              <p>{user.name}</p>
              </li>
          );
          })}
      </ul>
    </>
  );
}
 
export default Plans;
