import React, { useState, useEffect } from 'react';
import app from 'firebase/app';
import '../Testimonials/index.css';


export default function Form() {
    const [title, setTitle] = useState('');

    const handleOnChange = (e) => {
        setTitle(e.target.value);
    };
    // var Firebase = require('firebase/app');
    // require('firebase/auth');
    // require('firebase/database');   
    const createReview = () => {
        const reviewRef = app.database().ref('Review');
        const review = {
            title,
            username: currentUser.email,

        };

        reviewRef.push(review);
    };

    const [currentUser, setCurrentUser] = useState();
    useEffect(() =>{
        var app = require('firebase/app');
        require('firebase/auth');
        require('firebase/database');   
        app.auth().onAuthStateChanged((email) => {
            setCurrentUser(email)
        })
    }, [])
    return(
        <form>
            
            <textarea name="message" rows="2" class="question" id="msg" required autocomplete="off"></textarea>
            <label for="msg"><span>What's your message?</span></label>
            <input type="submit" value="Submit!" />
        </form>
        // <div>
        //     <input type="text" onChange={handleOnChange} value={title} />
        //     <button onClick={createReview}>Add a Review</button>
        // </div>

    );
}
